SELECT test.execute_step_id, test.execute_id, test.step_id, test.description, test.step_status, test.object_version_number, test.test_step, test.test_data, test.expected_result, defect.defect_type, defect.id defece_id, defect.defect_link_id, defect.issue_id defect_issue_id, attach.url, attach.attachment_name, attach.attachment_type, attach.id FROM ( SELECT cycle.execute_step_id, cycle.execute_id, cycle.step_id, cycle.description, cycle.step_status, cycle.object_version_number, step.test_step, step.test_data, step.expected_result, step.rank FROM ( select * from test_cycle_case_step
AND execute_step_id = #{dto.executeStepId}
AND execute_id = #{dto.executeId}
) cycle LEFT JOIN test_case_step step ON cycle.step_id = step.step_id ORDER BY step.rank
limit #{page} , #{pageSize}
) test LEFT JOIN test_cycle_case_attach_rel attach ON ( test.execute_step_id = attach.attachment_link_id AND attach.attachment_type = 'CYCLE_STEP' ) OR ( test.step_id = attach.attachment_link_id AND attach.attachment_type = 'CASE_STEP' ) LEFT JOIN test_cycle_case_defect_rel defect ON test.execute_step_id = defect.defect_link_id AND defect.defect_type = 'CASE_STEP'

SELECT test.execute_step_id, test.execute_id, test.step_id, test.description, test.step_status, test.object_version_number, test.test_step, test.test_data, test.expected_result, defect.defect_type, defect.id defece_id, defect.defect_link_id, defect.issue_id defect_issue_id, attach.url, attach.attachment_name, attach.attachment_type, attach.id FROM ( select * from( select tt.*,ROWNUM AS rowno from( SELECT cycle.execute_step_id, cycle.execute_id, cycle.step_id, cycle.description, cycle.step_status, cycle.object_version_number, step.test_step, step.test_data, step.expected_result, step.rank FROM ( select * from test_cycle_case_step
AND execute_step_id = #{dto.executeStepId}
AND execute_id = #{dto.executeId}
) cycle LEFT JOIN test_case_step step ON cycle.step_id = step.step_id ORDER BY step.rank ) tt
WHERE ROWNUM <= #{pageSize}
) table_alias
WHERE table_alias.rowno >= #{page}
) test LEFT JOIN test_cycle_case_attach_rel attach ON ( test.execute_step_id = attach.attachment_link_id AND attach.attachment_type = 'CYCLE_STEP' ) OR ( test.step_id = attach.attachment_link_id AND attach.attachment_type = 'CASE_STEP' ) LEFT JOIN test_cycle_case_defect_rel defect ON test.execute_step_id = defect.defect_link_id AND defect.defect_type = 'CASE_STEP'
